def validate_datos(materias:dict):
	"""
		Valida que se recuperen los datos parametros para generar
		los horarios como el limite de creditos 
	"""
	datos = []
	for clave,valor in materias.items():
		if clave=="0":
			datos.append(valor)
	return datos
def validate_lista(materias:dict):
	"""
		Valida que el diccionario sea pasado a lista correctamente
	"""
	materias_cursar = []
	for clave,valor in materias.items():
		if clave !="0":
			materias_cursar.append(valor)
	return materias_cursar

def validate_lista_menor(materiasPorCursar:list):
	"""
		Valida que la lista este ordenada por materias
		cuyos creditos esten de menor a mayor
	"""
	for dato in range(len(materiasPorCursar)-1,0,-1):
		for i in range(dato):
			if materiasPorCursar[i]['creditos']>=materiasPorCursar[i+1]['creditos']:
				#print('No')
				temp= materiasPorCursar[i]
				materiasPorCursar[i]=materiasPorCursar[i+1]
				materiasPorCursar[i+1]=temp
	return materiasPorCursar

def validate_lista_mayor(materiasPorCursar:list,l):
	"""
		Valida que la lista este ordenada por materias
		cuyos creditos esten de mayor a menor
	"""
	for dato in range(l-1,0,-1):
		for i in range(dato):
			if materiasPorCursar[i]['creditos']>=materiasPorCursar[i+1]['creditos']:
				#print('Si')
				temp= materiasPorCursar[i]
				materiasPorCursar[i]=materiasPorCursar[i+1]
				materiasPorCursar[i+1]=temp
	return materiasPorCursar

